import React, { useState } from "react";

export const Quiz = () => {
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Process the submitted answers here
    calculateScore();
  };

  const handleChange = (event, index) => {
    const { value } = event.target;
    setAnswers((prevAnswers) => {
      const updatedAnswers = [...prevAnswers];
      updatedAnswers[index] = value;
      return updatedAnswers;
    });
  };

  const calculateScore = () => {
    let correctAnswers = 0;
    const correctOptions = [
      "retirement",
      "pre-tax",
      "pre-tax",
      "contribution",
      "employer",
      "automatic",
      "tax-deferred",
      "withdrawal",
      "investment",
      "after-tax",
    ];

    for (let i = 0; i < answers.length; i++) {
      if (answers[i] && answers[i].toLowerCase() === correctOptions[i]) {
        correctAnswers++;
      }
    }

    const calculatedScore = (correctAnswers / correctOptions.length) * 100;
    setScore(calculatedScore);
  };

  return (
    <div className="quiz-container">
      <h1>Quiz Page</h1>
      <form onSubmit={handleSubmit}>
        <div className="questions-container">
          <div className="question">
            <label htmlFor="question1">A 401(k) is a type of __________ retirement savings account.</label>
            <input type="text" id="question1" name="question1" onChange={(event) => handleChange(event, 0)} />
          </div>

          {/* Add more questions here */}

          <div className="question">
            <label htmlFor="question10">Contributions made to a Roth 401(k) are __________.</label>
            <input type="text" id="question10" name="question10" onChange={(event) => handleChange(event, 9)} />
          </div>
        </div>

        <button type="submit">Submit</button>
      </form>

      {score !== null && (
        <div className="score">
          Your score: {score}%
        </div>
      )}
    </div>
  );
};

export default Quiz;
