import React, { useState, useEffect, useRef } from "react";
import menu from './menu.png';
import { Link } from "react-router-dom";

export const DropdownMenu = () => {
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);
  const menuTriggerRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        menuRef.current &&
        !menuRef.current.contains(event.target) &&
        !menuTriggerRef.current.contains(event.target)
      ) {
        setOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <div className="DropDownMenu">
      <div className="menu-container" ref={menuTriggerRef}>
        <div className="menu-trigger" onClick={() => setOpen(!open)}>
          <img src={menu} className="menu-icon" alt="Menu" />
        </div>

        <div className={`dropdown-menu ${open ? "active" : "inactive"}`} ref={menuRef}>
          <ul>
            <DropdownItem text="Home" path="/Home" />
            <DropdownItem text="Learning Modules" path="/Learning1" />
            <DropdownItem text="Quiz" path="/Quiz" />
            <DropdownItem text="Market Simulator Game" path="/Market" />
            <DropdownItem text="Chatbot" path="/Chatbot" />
          </ul>
        </div>
      </div>
    </div>
  );
};

function DropdownItem(props) {
  return (
    <li className="dropdownItem">
      <Link to={props.path}>{props.text}</Link>
    </li>
  );
}

export default DropdownMenu;
